import { useState } from "react";
import {wordSolution} from '../App.js';

const useGame = (wordSolution) => {
    const [turn, setTurn] = useState(0)
    const [currentGuess, setCurrentGuess] = useState('')
    const [guesses, setGuesses] = useState([]) //every guess is now an array
    const [history, setHistory] = useState([]) //every guess is changed to a string
    const [isCorrect, setIsCorrect] = useState(false)



    //Formats the users guess into an array of letter objects
    const formatUserGuess = () => {
        console.log('formatting user guess')
    }

    const addNewGuess = () => {

    }

    //handleKeyUp function handles user input from the user's keyboard
    //the key is passed into the .test method as an argument to make sure the user only types from A-Z
    const handleKeyUp = ({ key }) => {
        if (key === 'Enter') {
            if (turn > 5) {
                console.log('all guesses have been used')
                return
            }

            if (history.includes(currentGuess)) {
                console.log('you have already guessed that word!')
                return
            }

            if (currentGuess.length !== wordSolution.length) {
                console.log(`word must be ${wordSolution.length} chars long`)
                return

            }
            formatUserGuess()
        }
        
    //console.log({wordSolution})}
        if (key === 'Backspace') {
            setCurrentGuess(prev =>{
                return prev.slice(0, -1)
            })
            return
        }
        if (/^[A-Za-z]$/.test(key)) {
            //checks to see if the random word has less length than the user's current guess
            if(currentGuess.length < wordSolution.length) {
                setCurrentGuess((prev)=> {
                    return prev + key
                })
            }
        }
        
    }

    return {turn, currentGuess, guesses, isCorrect, handleKeyUp}

}

export default useGame;
