import React, { useState, useCallback } from 'react';
import Wordle from './components/Wordle';
import useGame from './hooks/useGame';

function App() {
	const [wordSolution, setWord] = useState('');
	const { resetGame } = useGame(wordSolution); // Get reset function

	const changeWordToShow = useCallback(() => {
		fetch('https://random-word-api.herokuapp.com/word')
			.then(response => response.json())
			.then(data => {
				if (data.length > 0) {
					const newWord = data[0];
					setWord(newWord);  // Set the new word
					resetGame(); // Reset the game state
				}
			})
			.catch(err => {
				console.log("Error " + err);
			});
	}, [resetGame]); // Add resetGame as a dependency

	return (
		<>
			<button type="button" onClick={changeWordToShow}>Change Word</button>
			{wordSolution && <Wordle wordSolution={wordSolution} />}
			Word name: {wordSolution} Length: {wordSolution.length}
		</>
	);
}

export default App;
